import React from 'react';
import { Header } from './Header';
import { TodoCounter } from './TodoCounter';
import { TodoSearch } from './TodoSearch';
import { CreateTodoButton } from './CreateTodoButton';
import { TodoList } from './TodoList';
import { TodoItem } from './TodoItem';
/* import './App.css'; */

const todos = [
  {text: "Cortar las cebollas", completed: true},
  {text: "Hervir el tomatesadasdas dasdasd as dasd asdsadas dsadas ", completed: false},
  {text: "Lavar las papas", completed: false},
];

function App() {
  const [searchValue, setSearchValue] = React.useState("");
  return (
    <>
    <Header />
    <TodoCounter />
    <TodoSearch 
      searchValue={searchValue}
      setSearchValue={setSearchValue}
    />
    <TodoList>
      {todos.map(todo=>( <TodoItem key={todo.text} text={todo.text} completed={todo.completed}/>))} 
    </TodoList>
  <CreateTodoButton />
    </> 

  );
}

export default App;
