import React from "react";
import { AppUI } from "./AppUI";
/* import './App.css'; */

const defaulttodos = [
  { text: "Cortar las cebollas", completed: false },
  {
    text: "Hervir el tomatesadasdas dasdasd as dasd asdsadas dsadas ",
    completed: false,
  },
  { text: "Lavar las papas", completed: false },
];

function App() {
  const [todos, setTodos] = React.useState(defaulttodos);
  const [searchValue, setSearchValue] = React.useState("");
  const completedTodos = todos.filter((t) => t.completed === true).length;
  const totalTodos = todos.length;

  let todoSearchbox = [];
  if (!searchValue >= 1) {
    todoSearchbox = todos;
  } else {
    todoSearchbox = todos.filter((t) => {
      const searchLower = searchValue.toLowerCase();
      const todosLower = t.text.toLowerCase();
      return todosLower.includes(searchLower);
    });
  }

  const completeTodos = (text) => {
    const todoIndex = todos.findIndex((t) => t.text === text);
    const newTodos = [...todos];
    newTodos[todoIndex].completed = !newTodos[todoIndex].completed;
    setTodos(newTodos);
  };
  const deleteTodos = (text) => {
    const todoIndex = todos.findIndex((t) => t.text === text);
    const newTodos = [...todos];
    newTodos.splice(todoIndex, 1);
    setTodos(newTodos);
  };

  return (
    <AppUI 
    totalTodos={totalTodos}
    completedTodos={completedTodos}
    searchValue={searchValue}
    setSearchValue={setSearchValue}
    todoSearchbox={todoSearchbox}
    onDelete={onDelete}
    onComplete={onComplete}


    />
  );
}

export default App;
