import React from "react";
import { Header } from "./Header";
import { TodoCounter } from "./TodoCounter";
import { TodoSearch } from "./TodoSearch";
import { CreateTodoButton } from "./CreateTodoButton";
import { TodoList } from "./TodoList";
import { TodoItem } from "./TodoItem";
/* import './App.css'; */

const defaulttodos = [
  { text: "Cortar las cebollas", completed: true },
  {
    text: "Hervir el tomatesadasdas dasdasd as dasd asdsadas dsadas ",
    completed: false,
  },
  { text: "Lavar las papas", completed: false },
];

function App() {
  const [todos, setTodos] = React.useState(defaulttodos);
  const [searchValue, setSearchValue] = React.useState("");
  const completedTodos = todos.filter((t) => t.completed === true).length;
  const totalTodos = todos.length;

  let todoSearchbox = [];
  if (!searchValue >= 1) {
    todoSearchbox = todos;
  } else {
    todoSearchbox = todos.filter((t) => {
      const searchLower = searchValue.toLowerCase();
      const todosLower = t.text.toLowerCase();
      return todosLower.includes(searchLower);
    });
  }

  const completeTodos = (text) => {
    const todoIndex = todos.findIndex((t) => t.text === text);
    todos[todoIndex].completed = true;
  };
  return (
    <>
      <Header />
      <TodoCounter total={totalTodos} completedTodos={completedTodos} />
      <TodoSearch searchValue={searchValue} setSearchValue={setSearchValue} />
      <TodoList>
        {todoSearchbox.map((todo) => (
          <TodoItem
            key={todo.text}
            text={todo.text}
            completed={todo.completed}
          />
        ))}
      </TodoList>
      <CreateTodoButton />
    </>
  );
}

export default App;
