import React from "react";
import { TodoContext } from "../TodoContext";

const TodoForm = () => {
  const [newTaskValue, setNewTaskValue] = React.useState("")
  const { modal, setModal, todoSearchbox, toggleModal } = React.useContext(TodoContext);
  const newTaskRead = (event)=>{
    setNewTaskValue(event.target.value)
      console.log(event.target.value)
  }
  const submitTask = (text) => {
    todoSearchbox.push({ text: text, completed: false });
    toggleModal();
  };
  return (
    <form className="todoCreator" onSubmit={submitTask}>
      <label className="Modal-label">Crea un nuevo ToDo!</label>
      <textarea
        className="newTask"
        id="newTask"
        placeholder="Sacar a pasear a mi perrito"
        value={newTaskValue}
        onChange={newTaskRead}
      ></textarea>
      <div className="button-Container">
        <button type="submit">Agregar</button>
        <button onClick={toggleModal} type="button">
          Cancelar
        </button>
      </div>
    </form>
  );
};
export { TodoForm };
