import React from "react";
import "./todoItem.css";

const TodoItem = (props) => {
  const taskComplete = (event) => {
      if (event.target.checked === true) {
          console.log("tarea lista");
      }else if(event.target.checked === false){
          console.log("Falta hacer esta tarea");
      }else{
          console.log("no sale esta wea");
      }
  };
  const taskIncomplete = () => {
          console.log("Cerrando")

  };
  return (
    <li className="todoItem">
      <input
        className="todoItem__checkbox"
        type="checkbox"
        onChange={taskComplete}
      ></input>
      <p className={`todoItem__task ${props.completed && "task-done"}`}>
        {props.text}
      </p>
      <span 
      className="todoItem__close"
      onClick={taskIncomplete}
      >X</span>
    </li>
  );
};

export { TodoItem };
