import React from "react";
import "./todoItem.css";

const TodoItem = (props) => {

  const probandoestawea = () => console.log(document.querySelector(".todoItem__checkbox").checked)
  document.querySelector(".todoItem__checkbox").checked = !document.querySelector(".todoItem__checkbox").checked
  return (
    <li className="todoItem">
      <input
        className="todoItem__checkbox"
        type="checkbox"
        onChange={props.onComplete}
      ></input>
      <p className={`todoItem__task ${props.completed && "task-done"}`} onClick={()=>probandoestawea()}>
        {props.text}
      </p>
      <span className="todoItem__close" onClick={props.onDelete}>
        X
      </span>
    </li>
  );
};

export { TodoItem };
