import React from "react";
import { TodoContext } from "../TodoContext";
import { Header } from "../Header";
import { TodoCounter } from "../TodoCounter";
import { TodoSearch } from "../TodoSearch";
import { CreateTodoButton } from "../CreateTodoButton";
import { TodoList } from "../TodoList";
import { TodoItem } from "../TodoItem";
import { Modal } from "../Modal";

const AppUI = () => {
  const closeModal = () => {
    setModal(!modal);
  };
  const submitTask = () => {
    let newTaskSearch = document.getElementById("newTask").value;
    todoSearchbox.push({ text: newTaskSearch, completed: false });
    closeModal();
  };
  const {
    error,
    loading,
    todoSearchbox,
    deleteTodos,
    completeTodos,
    modal,
    setModal,
  } = React.useContext(TodoContext);
  return (
    <>
      <Header />
      <TodoCounter />
      <TodoSearch />

      <TodoList>
        {error && <p>Algo sali√≥ mal! :c</p>}
        {loading && <p>Estamos cargando!</p>}

        {!loading && !!todoSearchbox.length
          ? todoSearchbox.map((todo) => (
              <TodoItem
                key={todo.text}
                text={todo.text}
                completed={todo.completed}
                onDelete={() => deleteTodos(todo.text)}
                onComplete={() => completeTodos(todo.text)}
              />
            ))
          : !loading && !todoSearchbox.length && <p>Crea tu primer ToDo!</p>}
      </TodoList>

      {modal && (
        <Modal>
          <form className="todoCreator" onSubmit={submitTask}>
            <label className="Modal-label">Crea un nuevo ToDo!</label>
            <textarea
              className="newTask"
              id="newTask"
              placeholder="Sacar a pasear a mi perrito"
            ></textarea>
            <div className="button-Container">
              <button type="submit">
                Agregar
              </button>
              <button onClick={closeModal} type="button">Cancelar</button>
            </div>
          </form>
        </Modal>
      )}

      <CreateTodoButton />
    </>
  );
};
export { AppUI };
