import React from "react";
import { AppUI } from "./AppUI";
/* import './App.css'; */

/* const defaulttodos = [
  { text: "Cortar las cebollas", completed: false },
  {
    text: "Hervir el tomatesadasdas dasdasd as dasd asdsadas dsadas ",
    completed: false,
  },
  { text: "Lavar las papas", completed: false },
]; */
/* const defaultstring = JSON.stringify(defaulttodos) */
function useLocalStorage(itemName, InitialValue) {
  const localStorageItem = localStorage.getItem(itemName);
  let parsedItem;
  if (localStorageItem) {
    parsedItem = JSON.parse(localStorageItem);
  } else {
    localStorage.setItem(itemName, JSON.stringify(InitialValue));
    parsedItem = InitialValue;
  }
  const [item, setTodos] = React.useState(parsedItem);

  const saveTodos = (newItem) => {
    const stringifiedItem = JSON.stringify(newItem);
    localStorage.setItem(itemName, stringifiedItem);
    setItem(newItem);
  };
  return [item, saveTodos]
}
function App() {
  const [todos, saveTodos] = useLocalStorage("TODOS_V1", []);
  const [searchValue, setSearchValue] = React.useState("");
  const completedTodos = todos.filter((t) => t.completed === true).length;
  const totalTodos = todos.length;

  let todoSearchbox = [];
  if (!searchValue >= 1) {
    todoSearchbox = todos;
  } else {
    todoSearchbox = todos.filter((t) => {
      const searchLower = searchValue.toLowerCase();
      const todosLower = t.text.toLowerCase();
      return todosLower.includes(searchLower);
    });
  }

  const completeTodos = (text) => {
    const todoIndex = todos.findIndex((t) => t.text === text);
    const newTodos = [...todos];
    newTodos[todoIndex].completed = !newTodos[todoIndex].completed;
    saveTodos(newTodos);
  };
  const deleteTodos = (text) => {
    const todoIndex = todos.findIndex((t) => t.text === text);
    const newTodos = [...todos];
    newTodos.splice(todoIndex, 1);
    saveTodos(newTodos);
  };

  return (
    <AppUI
      totalTodos={totalTodos}
      completedTodos={completedTodos}
      searchValue={searchValue}
      setSearchValue={setSearchValue}
      todoSearchbox={todoSearchbox}
      deleteTodos={deleteTodos}
      completeTodos={completeTodos}
    />
  );
}

export default App;
